import React, {Component} from 'react';
import {Login} from '../components/Login';
import {Register} from '../components/Register';
import { Nav } from '../components/Nav';
import {Switch, Route} from 'react-router-dom'
import { Home } from '../components/Home';
import { registerObj } from '../utils/regOpr';
import { registerOperations } from '../utils/dbOpr';

export class MusicApp extends Component{
    constructor(props){
        super(props);
        this.state = {msg: ""}
    }
    onRegister(event){
        event.preventDefault();
        var name = event.target.querySelector('#name').value;
        var username = event.target.querySelector('#username').value;
        var pwd = event.target.querySelector('#pwd').value;
        var userObject = new registerObj(name,username,pwd);
        registerOperations.addUser(userObject);
        this.setState({msg: "Registered successfully"})
    }
    render(){
        return(
            <div>
                <Nav/>
                <Switch>
                    <Route exact path = "/" component = {Home}></Route>
                    <Route path = "/login" component = {Login}></Route>
                    <Route path = "/register" render = {(props)=>{
                    return <Register msg = {this.state.msg} reg = {this.onRegister.bind(this) }/>
                    }}></Route>
                </Switch>
            </div>
        )
    }
}
