import * as firebase from 'firebase'

export const registerOperations = {
  
    addUser(userObj){
        var promise =  firebase.database().ref("/users"+userObj.username).set(userObj);
        promise.then(data=> {
            return data
         }).catch(error=>{
             return error;
             // console.log(error);
         })
     }
 }
